import React from "react";
import socket from "../../socket";
import { useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import Animation from "../tools/Animation";

const Modals = ({
  roomSessionKey,
  playerSessionKey,
  setSelectedPlayers,
  selectedPlayers,
  setShowLeaderVoteModal,
  leaderVotedPlayers,
  setShowQuestVoteModal,
  setShowQuestVoteButton,
  setShowVoteModal,
  setShowPlayersVote,
  players,
  type,
  missionTeamSizes,
  setShowExit,
  showExit,
  showQuestVoteModal,
  showLeaderVoteModal,
  ShowVoteModal,
}) => {
  const navigate = useNavigate();
  const { t } = useTranslation();

  if (type === "voteAll") {
    // Vote Medal
    return (
      <Animation show={ShowVoteModal}>
        <div className="flex items-center justify-center z-50">
          <div className="bg-gray-800 rounded-lg p-6 w-80 max-w-md">
            <div className="mb-5">
              <h3 className="text-xl font-bold text-white text-center ">
                {t("modals.voteQuestTeam")}
              </h3>
              <div className="font-semibold text-white text-center">
                {t("modals.choose")} {missionTeamSizes}
              </div>
            </div>

            <div className="space-y-2 mb-4 flex flex-col items-center">
              {players.map((player) => (
                <label
                  key={player.playerSessionKey}
                  className="gap-x-3 flex flex-row items-center text-white"
                >
                  <input
                    disabled={
                      // Disable only unchecked boxes when limit is reached
                      !selectedPlayers.includes(player.playerSessionKey) &&
                      selectedPlayers.length >= missionTeamSizes
                    }
                    type="checkbox"
                    checked={selectedPlayers.includes(player.playerSessionKey)}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedPlayers([
                          ...selectedPlayers,
                          player.playerSessionKey,
                        ]);
                      } else {
                        setSelectedPlayers(
                          selectedPlayers.filter(
                            (id) => id !== player.playerSessionKey
                          )
                        );
                      }
                    }}
                    className="appearance-none h-5 w-5 border-2 border-blue-500 rounded-full checked:bg-blue-500 checked:border-transparent transition duration-200 cursor-pointer "
                  />
                  {player.name}
                </label>
              ))}
            </div>
            <div className="flex gap-2 justify-center">
              <button
                disabled={selectedPlayers.length !== missionTeamSizes}
                onClick={() => {
                  // Send vote to server
                  socket.emit("vote_team", {
                    roomSessionKey,
                    playerSessionKey,
                    selectedPlayers,
                  });
                  setShowVoteModal(false);
                  setShowPlayersVote(false);
                  setSelectedPlayers([]);
                }}
                className={`px-4 py-2  text-white rounded-lg
                ${
                  selectedPlayers.length !== missionTeamSizes
                    ? "bg-amber-600/25 backdrop-blur-md border-amber-400/20 cursor-not-allowed"
                    : "bg-amber-600  hover:bg-amber-700"
                }`}
              >
                {t("modals.submitVote")}
              </button>

              <button
                onClick={() => {
                  setShowVoteModal(false);
                  setSelectedPlayers([]);
                }}
                className="px-4 py-2 bg-gray-600 hover:bg-gray-700 text-white rounded-lg"
              >
                {t("modals.cancel")}
              </button>
            </div>
          </div>
        </div>
      </Animation>
    );
  } else if (type === "leaderVote") {
    // Leader selecting
    return (
      <Animation show={showLeaderVoteModal}>
        <div className="flex items-center justify-center z-50">
          <div className="bg-gray-800 rounded-lg p-6 w-80 max-w-md">
            <div className="mb-5">
              <h3 className="text-xl font-bold text-white text-center ">
                {t("modals.selectQuestTeam")}
              </h3>
              <div className="font-semibold text-white text-center">
                {t("modals.choose")} {missionTeamSizes}
              </div>
            </div>

            <div className="space-y-2 mb-4 flex flex-col items-center">
              {players.map((player) => (
                <label
                  key={player.playerSessionKey}
                  className="gap-x-3 flex flex-row items-center text-white"
                >
                  <input
                    disabled={
                      // Disable only unchecked boxes when limit is reached
                      !selectedPlayers.includes(player.playerSessionKey) &&
                      selectedPlayers.length >= missionTeamSizes
                    }
                    type="checkbox"
                    checked={selectedPlayers.includes(player.playerSessionKey)}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedPlayers([
                          ...selectedPlayers,
                          player.playerSessionKey,
                        ]);
                      } else {
                        setSelectedPlayers(
                          selectedPlayers.filter(
                            (id) => id !== player.playerSessionKey
                          )
                        );
                      }
                    }}
                    className="appearance-none h-5 w-5 border-2 border-blue-500 rounded-full checked:bg-blue-500 checked:border-transparent transition duration-200 cursor-pointer "
                  />
                  {player.name}
                </label>
              ))}
            </div>
            <div className="flex gap-2 justify-center">
              <button
                disabled={selectedPlayers.length !== missionTeamSizes}
                onClick={() => {
                  // Send vote to server
                  setShowLeaderVoteModal(false);
                  setShowVoteModal(false);
                  setShowQuestVoteButton(false);
                  setSelectedPlayers([]);
                  socket.emit("leader_vote", {
                    roomSessionKey,
                    playerSessionKey,
                    selectedPlayers,
                  });
                }}
                className={`px-4 py-2  text-white rounded-lg
                ${
                  selectedPlayers.length !== missionTeamSizes
                    ? "bg-amber-600/25 backdrop-blur-md border-amber-400/20 cursor-not-allowed"
                    : "bg-amber-600  hover:bg-amber-700"
                }`}
              >
                {t("modals.submitVote")}
              </button>

              <button
                onClick={() => {
                  setShowLeaderVoteModal(false);
                  setSelectedPlayers([]);
                }}
                className="px-4 py-2 bg-gray-600 hover:bg-gray-700 text-white rounded-lg"
              >
                {t("modals.cancel")}
              </button>
            </div>
          </div>
        </div>
      </Animation>
    );
  } else if (type === "questVote") {
    return (
      // Players vote to proceed
      <Animation show={showQuestVoteModal}>
        <div className="flex items-center justify-center z-50">
          <div className="bg-gray-800 rounded-lg p-6 w-50 max-w-md">
            <h3 className="text-xl text-center font-bold mb-4 text-white">
              {t("modals.proceedToQuest")}
            </h3>
            <div className="space-y-2 mb-4">
              <label className="flex justify-center gap-10 text-white">
                <button
                  onClick={() => {
                    const vote = "success";
                    socket.emit("vote_quest", {
                      roomSessionKey,
                      playerSessionKey,
                      vote,
                      leaderVotedPlayers,
                    });
                    setShowQuestVoteModal(false);
                    setShowQuestVoteButton(false);
                  }}
                  className="px-4 py-2 bg-amber-600 hover:bg-amber-700 text-white rounded-lg"
                >
                  {t("modals.yes")}
                </button>
                <button
                  onClick={() => {
                    const vote = "fail";
                    socket.emit("vote_quest", {
                      roomSessionKey,
                      playerSessionKey,
                      vote,
                      leaderVotedPlayers,
                    });
                    setShowQuestVoteModal(false);
                    setShowQuestVoteButton(false);
                  }}
                  className="px-4 py-2 bg-gray-600 hover:bg-gray-700 text-white rounded-lg"
                >
                  {t("modals.no")}
                </button>
              </label>
            </div>
          </div>
        </div>
      </Animation>
    );
  } else if (type === "exit") {
    return (
      // Player exiting game
      <Animation show={showExit}>
        <div className="bg-gray-700 rounded-lg p-6 w-50 max-w-md">
          <h3 className="text-xl text-center font-bold mb-4 text-white">
            {t("modals.exitGame")}
          </h3>
          <div className="space-y-2 mb-4">
            <label className="flex gap-10 justify-center text-white">
              <button
                onClick={() => {
                  socket.emit("exit", {
                    roomSessionKey,
                    playerSessionKey,
                  });
                  sessionStorage.removeItem("playerSessionKey");
                  sessionStorage.removeItem("roomSessionKey");
                  navigate(`/`);
                }}
                className="px-4 py-2 bg-gray-500 hover:bg-gray-600 text-white rounded"
              >
                {t("modals.yes")}
              </button>
              <button
                onClick={() => {
                  setShowExit(false);
                }}
                className="px-4 py-2 bg-gray-500 hover:bg-gray-600 text-white rounded"
              >
                {t("modals.no")}
              </button>
            </label>
          </div>
        </div>
      </Animation>
    );
  }
};

export default Modals;
