import { io } from "socket.io-client";

const socket = io(process.env.REACT_APP_SERVER_URL || "http://localhost:4000", {
  transports: ["websocket"], // helps with CORS
});

// // Use environment variable first, fallback only for local dev
// const socket = io("https://sabotage.onrender.com", {
//   transports: ["websocket"], // helps avoid CORS issues
// });

// Try reconnecting with stored keys
const roomKey = localStorage.getItem("roomSessionKey");
const playerKey = localStorage.getItem("playerSessionKey");

if (roomKey && playerKey) {
  socket.auth = { roomKey, playerKey };
}

socket.connect();

export default socket;
